INSERT INTO `san_pham` (`ma`, `ten`, `ma_loai_san_pham`, `ma_nha_san_xuat`, `cau_hinh`, `mo_ta`, `don_gia`, `giam_gia`, `hinh`, `list_hinh`, `so_luong`, `bao_hanh`, `trang_thai`, `so_lan_mua`, `luot_xem`, `sao`) VALUES
(1, 'Mainboard ASUS ROG STRIX B360-G GAMING', 1, 10, '                                        <tr><td> Kích thước </td><td> Micro-ATX</td></tr>\r\n<tr><td> Socket</td><td> LGA 1151-v2 </td> </tr>\r\n<tr><td> Chipset</td><td> B360 </td> </tr>\r\n<tr><td> Khe RAM tối đa</td><td>4 khe</td> </tr>\r\n<tr><td> Hỗ trợ bộ nhớ tối đa</td><td>64GB</td> </tr>\r\n<tr><td> Bus RAM hỗ trợ </td><td>2133MHz ; 2400MHz ; 2666MHz </td> </tr>\r\n<tr><td> Lưu trữ</td><td1 x M.2 NVMe ; 1 x M.2 SATA/NVMe ; 6 x SATA 3 6Gb/s ; Hỗ trợ Intel Optane </td> </tr>\r\n<tr> <td> Kiểu RAM hỗ trợ</td><td> DDR4</td> </tr>\r\n<tr> <td> Kiểu khe M.2 hỗ trợ</td><td> M.2 SATA/NVMe</td>  </tr>\r\n<tr> <td> Cổng xuất hình</td> <td>1 x DVI-D ; 1 x HDMI </td> </tr>\r\n<tr><td> Khe PCI</td> <td> - 1 x PCIe 3.0 x16 <br>- 1 x PCIe 3.0 x16 <br>- 2 x PCIe 3.0 x1</td> </tr>\r\n<tr><td> Multi-GPU </td><td> AMD CrossFire </td> </tr>\r\n<tr><td> Đèn LED </td><td> Đơn sắc </td> </tr>\r\n<tr><td> Số cổng USB </td><td> - 4 x USB 3.1 (tối đa 6) <br>- 2 x USB 2.0 (tối đa 6)</td> </tr>\r\n<tr><td> LAN</td><td>  LAN 1 Gb/s </td> </tr>\r\n<tr><td> Âm thanh </td><td> ROG SupremeFX 7.1-channel </td> </tr>\r\n                                    ', '                                        <p>Bo mạch chính/ Mainboard Asus Rog Strix B360-G Gaming đóng vai trò là một bản mạch  trung gian giao tiếp giữa các thiết bị với nhau. Thiết bị là mạch điện chính của một hệ thống hay thiết bị điện tử (ví dụ như nguồn máy tính, CPU, bo mạch đồ họa, bo mạch âm thanh, bo mạch mạng, ổ cứng, màn hình, máy in, máy  quét,…) bằng cách trực tiếp trên bề mặt thiết bị đó hoặc thông qua các kết nối cắm vào hoặc dây dẫn liên kết.</p>\r\n\r\n<p>Chipset  Intel®  B360 trong bo mạch chủ Mainboard Asus Rog Strix B360-G Gaming giữ chức năng rất quan trọng. Chipset đưa dữ liệu từ đĩa cứng qua bộ nhớ rồi tới CPU (Intel® cho Intel Core™ Thế hệ thứ 8 Bộ vi xử lý và Hỗ trợ công nghệ Intel® Turbo Boost 2.0 ) và đảm bảo các thiết bị ngoại vi và các card mở rộng đều có thể thể “giao tiếp và hiểu” được với CPU và các thiết bị khác. Thiết bị hỗ trợ cổng USB  tối đa 6 USB 3.1, 6 USB 2.0 với bộ nhớ tối đa 64 GB.</p>\r\n\r\n<p>Với thiết kế dành riêng cho bộ xử lý Intel® Core ™ thế hệ thứ 8, thiết bị có khả năng tối đa hóa khả năng kết nối và tốc độ bằng khe cắm LGA1151 sẵn sàng cho bộ vi xử lý máy tính. Điểm cộng của sản phẩm nằm ở  hệ thống Aura Sync RGB- giúp đồng bộ hệ thống chiếu sáng LED với nhiều danh mục thiết bị PC tương thích, bao gồm các dải RGB. Ngoài ra, âm thanh chơi game SupremeFX S1220A kết hợp với Sonic Studio III tạo ra những cảnh quan âm thanh thiên nhiên đầy thu hút, tạo trải nghiệm đa dạng về âm thanh cho người dùng. Cấu hình làm mát tiên tiến với chức năng tinh chỉnh toàn hệ thống tự động được thiết kế riêng cho thiết bị. Cuối cùng chính là khả năng kết nối gaming thông qua các đầu cắm M.2 kép và USB 3.1 thế hệ 2 loại A; song song với Intel Gigabit Ethernet, LANGuard và GameFirst mang đến trải nghiệm chơi trực tuyến mượt mà hơn, an toàn hơn và không bị giật.   </p>                                                                                                                                             ', 2300000, 0, '1-Aus-M1-1.png', '[\"1-Aus-M1-2.png\",\"1-Aus-M1-4.png\",\"1-Aus-M1-4.png\"]', 100, 36, 1, 1, 23, 5),
(2, 'Mainboard ASUS PRIME B450M-A', 1, 10, '                                        <tr><td> Kích thước </td><td> Micro-ATX</td></tr>\r\n<tr><td> Socket</td><td> AM4 </td> </tr>\r\n<tr><td> Chipset</td><td> B450 </td> </tr>\r\n<tr><td> Khe RAM tối đa</td><td>4 khe</td> </tr>\r\n<tr><td> Hỗ trợ bộ nhớ tối đa</td><td>64GB</td> </tr>\r\n<tr><td> Bus RAM hỗ trợ </td><td>2133MHz ; 2400MHz ; 2666MHz ; 2800MHz ; 3000MHz ; 3200MHz ; 3466MHz </td> </tr>\r\n<tr><td> Lưu trữ</td><td> 1 x M.2 SATA ; 1 x M.2 SATA/NVMe ; 6 x SATA 3 6Gb/s </td> </tr>\r\n<tr> <td> Kiểu RAM hỗ trợ</td><td> DDR4</td> </tr>\r\n<tr> <td> Kiểu khe M.2 hỗ trợ</td><td> M.2 SATA/NVMe</td>  </tr>\r\n<tr> <td> Cổng xuất hình</td> <td>1 x DVI-D ; 1 x HDMI ; 1 x VGA/D-sub </td> </tr>\r\n<tr><td> Khe PCI</td> <td> - 3 x PCIe 3.0 x16<br>- 2 x PCIe 2.0 x1</td> </tr>\r\n<tr><td> Đèn LED </td><td> Đơn sắc </td> </tr>\r\n<tr><td> Số cổng USB </td><td> - 4 x USB 3.1 (tối đa 8)<br>- hỗ trợ tối đa 4 x USB 2.0 </td> </tr>\r\n<tr><td> LAN</td><td>  1 x LAN 1 Gb/s </td> </tr>\r\n<tr><td> Âm thanh </td><td> Realtek ALC887 7.1-channel </td> </tr>\r\n                                    ', '                                        <p>-Bo mạch chủ AMD AM4 mATX với Aura Sync RGB, DDR4 3466MHz, M.2, HDMI 2.0b, SATA 6Gbps và USB 3.1 Gen 2</p>\r\n<p>-Fan Xpert 2+: Điều khiển linh hoạt để làm mát và làm mát tối đa, cộng với cảm biến nhiệt độ GPU để khi chơi ngững game nặng nhiệt độ sẽ mát hơn.</p>\r\n<p>-Kết nối cực nhanh: Tính linh hoạt và khẳ năng truyền dẫn cao với USB 3.1 Gen 2 và M.2</p>\r\n<p>-ASUS Aura Sync: Đầu nối trên bo mạch có đèn LED RGB , dễ dàng đồng bộ hóa với phần cứng có khả năng tương thích với Aura Sync</p>\r\n<p>-Bảo vệ 5X III: Nhiều biện pháp bảo vệ phần cứng để bảo vệ hệ thống an toàn toàn diện</p>\r\n<p>-Dòng bo mạch chủ ASUS Prime B450 cung cấp nền tảng và sự hỗ trợ tối ưu cho các linh kiện trong hệ thống PC của bạn. Nó đã kết hợp tất cả những linh kiện có chất lượng tốt nhất để hỗ trợ tối đa dành riêng cho bộ vi xử lý AMD Ryzen ™ với thiết kế hiện đại của ASUS, vì vậy bạn sẽ được hưởng lợi từ các công nghệ hàng đầu – bao gồm điều chỉnh hệ thống tự động, điều khiển làm mát toàn diện và âm thanh tích hợp. Khi bạn xây dựng  hệ thống với bo mạch chủ ASUS Prime B450 Series, sẽ là một sự lựa chọn thông minh, dễ dàng và tiết kiệm chi phí.</p>\r\n<p>-Hiệu quả năng lượng toàn diện: Với bộ xử lí năng lượng (EPU). Bạn sẽ có được khẳ năng tiết kiệm năng lượng trên toàn thống. EPU sẽ tự động tối ưu mức tiêu thụ một cách hợp lý nhất để có được hiệu năng cao nhất trên toàn hệ thống mà không lãng phí năng lượng.</p>\r\n<p>-Điều chỉnh làm mát linh hoạt: Dòng bo mạch chủ ASUS Prime B450 tích hợp khẳ năng điều khiển hệ thống làm mát thông qua Fan Expert 2+. Chế  độ cho phép điều chỉnh cấu hình thông minh chỉ bằng 1 lần nhấp chuột.</p>\r\n<p>-Dòng sản phẩm Prime B450 có tính năng điều khiển làm mát toàn diện nhất, có thể điều chỉnh cấu hình thông qua phần mềm ASUS Fan Xpert hoặc BIOS UEFI.</p>\r\n<p>-Mỗi danh mục có thể được thiết lập để theo dõi và phản ứng với tối đa ba cảm biến nhiệt. Thông qua Fan Xpert, bạn cũng có thể chỉ định một cảm biến để theo dõi nhiệt độ của card đồ họa ASUS được hỗ trợ để đảm bảo làm mát tối ưu trong các khối lượng công việc của GPU hoặc CPU.</p>\r\n<p>-Mỗi CHA FAN trên bo mạch đều hỗ trợ chế độ PWM và DC.</p>\r\n<p>-Tăng tốc RAM của bạn : Với một loạt các chỉnh sửa trong thiết kế để cải thiện độ ổn định của RAM, dòng sản phẩm PRIME B450 cung cấp một nền tảng mạnh cho hệ thống của bạn. Với sự hỗ trợ toàn diện cho toàn bộ cấu hình bộ nhớ tưng thích với CPURyzen sẽ giúp RAM của bạn hoạt động với băng thông cao nhất. Dòng PRIME B450 đem lại khẳ năng tối ưu hóa cho và môi trường làm việc tốt nhất cho các linh kiện trên bo mạch chủ và CPU Ryzen.</p>\r\n<p>-BIOS UEFI là điểm đầu tiên trong bất kỳ quá trình xây dựng PC nào và Series B450 Prime có một trong những tính năng tốt nhất.</p>\r\n<p>-Q-Fan Control có thể cấu hình tốc độ hoạt động điều chỉnh bằng tay, hoặc sử dụng cấu hình quạt được cấu hình sẵn để chọn chế độ Standard, Silent, Turbo hoặc Full Speed, cùng với công tắc tự động ở tốc độ tối đa nếu nhiệt độ đạt 75 ° C.</p>\r\n<p>-USB 3.1 Gen 2 Type-A™ tốc độ tối đa 10Gbps :Với USB 3.1 Gen 2 Type-A™ được tich hợp, bạn sẽ trải nghiệm tính linh hoạt kết nối với các thiết bị ngoại vi và tốc độ truyền dữ liệu cực nhanh lên tới 10Gbps – gấp đôi so với chuẩn USB 3.1 Gen 1.</p>\r\n<p>-USB 3.1 Gen 1, với hai cổng được tích hợp cho bảng mặt trước. Trải nghiệm tốc độ truyền dữ liệu nhanh hơn 10 lần so với USB 2.0 với kết nối cắm và chạy ngay lập tức. Rất thuận tiện và đang là chuẩn USB phổ biến nhất nhất.</p>\r\n<p>-Tăng tốc với chuẩn M.2: Với x4 PCI Express® 3.0/2.0, M.2 hỗ trợ lên đến tốc độ truyền dữ liệu 32Gbps. Đó là sự lựa chọn hoàn hảo cho một hệ điều hành hoặc ổ đĩa ứng dụng, cung cấp truy cập nhanh vào dữ liệu.</p>\r\n<p>-AMD StoreMI: Công nghệ AMD StoreMI là một tiện ích phần mềm giúp tăng tốc ổ đĩa để đáp ứng hệ thống tốt hơn với thời gian tải khởi động và phần mềm giảm đi đáng kể. Nó kết hợp hiệu suất SSD với khả năng lưu trữ dung lượng lớn của một HDDbằng cách tự động di chuyển dữ liệu thường xuyên truy cập vào bộ nhớ SSD chuyên dụng.</p>\r\n<p>-Chipset AMD B450 ChipsetAMD B450 mang lại hiệu năng cao và linh hoạt với bộ xử lý AMD® AM4-socket Ryzen ™ mới nhất. Nó cũng cung cấp công nghệ tăng tốc lưu trữ AMD StoreMI và cung cấp hai cổng USB 3.0 Gen2 10Gbps, hai cổng USB 3.1 Gen1 và bốn cổng SATA 6Gbps để truy xuất dữ liệu nhanh hơn. </p>                                                               ', 1900000, 1000, '2-Aus-M1-1.jpg', '[\"2-Aus-M1-2.jpg\",\"2-Aus-M1-3.jpg\",\"2-Aus-M1-4.jpg\"]', 100, 36, 1, 1, 1, 0),
(3, 'Mainboard ASUS ROG RAMPAGE VI EXTREME OMEGA', 1, 10, '<tr><td> Kích thước </td><td> Extended-ATX</td></tr>\r\n<tr><td> Socket</td><td> LGA 2066</td> </tr>\r\n<tr><td> Chipset</td><td> X299 </td> </tr>\r\n<tr><td> Khe RAM tối đa</td><td>8 khe</td> </tr>\r\n<tr><td> Hỗ trợ bộ nhớ tối đa</td><td>128GB</td> </tr>\r\n<tr><td> Bus RAM hỗ trợ </td><td>2133MHz ; 2400MHz ; 2666MHz ; 2933MHz ; 3000MHz ; 3200MHz ; 3300MHz ; 3333MHz ; 3400MHz ; 3466MHz ; 3600MHz ; 3733MHz ; 3800MHz ; 3866MHz ; 4000MHz ; 4133MHz ; 4266MHz </td> </tr>\r\n<tr><td> Lưu trữ</td><td> 1 x M.2 SATA/NVMe ; 2 x M.2 NVMe ; 6 x SATA 3 6Gb/s </td> </tr>\r\n<tr> <td> Kiểu RAM hỗ trợ</td><td> DDR4</td> </tr>\r\n<tr> <td> Kiểu khe M.2 hỗ trợ</td><td> M.2 SATA/NVMe</td>  </tr>\r\n<tr> <td> Cổng xuất hình</td> <td>1 x DVI-D ; 1 x HDMI </td> </tr>\r\n<tr><td> Khe PCI</td> <td> 3 x PCIe 3.0 x16<br>1 x PCIe 3.0 x4 </td> </tr>\r\n<tr><td> Multi-GPU </td><td> AMD CrossFire ; NVIDIA SLI </td> </tr>\r\n<tr><td> Đèn LED </td><td> RGB </td> </tr>\r\n<tr><td> Số cổng USB </td><td> -10 x USB 3.1 Gen 1 ( tối đa 14)<br>-1 x USB 3.1 Gen 2 ( tối đa 3)<br>-4 x USB 2.0 ( tối đa 4) </td> </tr>\r\n<tr><td> LAN</td><td>  2 x LAN 1 Gb/s </td> </tr>\r\n<tr><td> Kết nối không dây </td><td>  Bluetooth 5.0 ; WiFi 802.11 a/b/g/n/ac </td> </tr>\r\n<tr><td> Âm thanh </td><td> ROG SupremeFX 8-Channel High Definition Audio CODEC S1220</td> </tr>\r\n', '<p>-ROG RAMPAGE VI EXTREME OMEGA là đại diện đầu bảng của dòng mainboard 2066 đến từ ASUS, hỗ trợ các dòng CPU Core X mạnh mẽ nhất của Intel cộng thêm đầy đủ các tính năng mà bất kì tín đồ công nghệ nào cũng thèm muốn trên bộ chipset X299. Đem lại trải nghiệm sử dụng tốt nhất từ phía Intel.</p>\r\n<p>-Thiết kế: Với việc đạt chuẩn kích thước E-ATX, cho phép ROG RAMPAGE VI EXTREME OMEGA hỗ trợ rất nhiều tính năng cao cấp như nút nguồn, reset, khả năng cấp nguồn lớn... chưa kể đến khả năng đem lại một số lượng các cổng kết nối khổng lồ mà chắc chắn rất ít ai có thể tận dụng hết.</p>\r\n<p>-Hiệu năng: Được trang bị với bộ cấp nguồn lên tới 16phase, với mỗi phase có khả năng chịu tải với cường độ dòng điện tới 45A, giúp đem lại hiệu năng hoạt động ổn định nhất cho các dòng CPU Core X đặc biệt đối với những tín đồ công nghệ yêu thích ép xung.\r\n<p>-Nhờ vào bo mạch được thiết kế tối ưu, hỗ trợ ép xung bộ nhớ lên tới tốc độ 4266MHz, giúp người dùng tận dụng tối đa hiệu năng của các dòng CPU Core X cao cấp.</p>\r\n<p>-Kết nối: ROG RAMPAGE VI EXTREME OMEGA sẽ đem lại cho người dùng khả năng mở rộng phần cứng tối đa nhất, bất kì thứ gì mà bạn có thể nghĩ tới ổ cứng, card màn hình, kết nối mạng, âm thanh..... chắc chắn bạn sẽ ngạc nhiên về những gì mà chiếc mainboard này có thể làm được.</p>\r\n<p>-Sử dụng hai bộ xử lý mạng Intel I219V (1Gb/s) và Aquantia AQC-107 (10Gb/s), với độ trễ cực thấp, giúp đáp ứng tốt cả về nhu cầu chơi game lẫn việc truyền dữ liệu.</p>\r\n<p>-Âm thanh cũng là một điểm sáng giá của ROG RAMPAGE VI EXTREME OMEGA, với bộ giải mã âm thanh SupremeFX S1220 và bộ chuyển đổi tín hiệu ESS SABRE9018Q2C kết hợp với mạch xư lý âm thanh được tách biệt hoàn toàn so với phần còn lại của mainboard, giúp đem lại trải nghiệm âm thanh tốt nhất cho người dùng.</p>\r\n<p>-Về lưu trữ, ROG RAMPAGE VI EXTREME OMEGA được tích hợp tới 3 khe cắm M.2 NVMe với tốc độ lên tới 32Gb/s, sẵn sàng hỗ trợ cho các ổ cứng M.2 SSD cao cấp.</p>\r\n<p>-Tản nhiệt: Với khả năng hỗ trợ mở rộng kèm theo công suất hoạt động lớn, việc sử dụng hệ thống tản nhiệt đồ sộ là một điều cực kì quan trọng đối với bất kì chiếc mainboard nào như ROG RAMPAGE VI EXTREME OMEGA.</p>\r\n<p>-Nhằm đảm bảo hiệu năng cũng như khả năng cấp nguồn cho CPU, trên toàn bộ bộ cấp nguồn của ROG RAMPAGE VI EXTREME OMEGA đều được tiếp xúc trực tiếp một lớp tản nhiệt lớn với hai quạt làm mát, giúp đem lại nhiệt ođọ hoạt động luôn ở trạng thái ổn định nhất.</p>\r\n<p>-Ngoài ra, ROG RAMPAGE VI EXTREME OMEGA còn được trang bị rất nhiều chân cắm 4pin và các cảm biến nhiệt độ trên mainboard, giúp người dùng có thể theo dõi nhiệt độ và điều tốc quạt một cách chính xác nhất.</p>\r\n<p>-Một điều đặc biệt khác của ROG RAMPAGE VI EXTREME OMEGA là chiếc fan hub được tặng kèm, giúp người dùng có thể kết nối và điều khiển tập trung các quạt làm mát của hệ thống vào chung một chỗ, cực kì thuận tiện.</p>\r\n\r\n<p>-Tối ưu trải nghiệm: Tất cả các dòng sản phẩm của ASUS, đặc biệt là dòng ROG đều được thiết kế với nguyên lý đặt trải nghiệm của người sử dụng lên trên hết. Đến với dòng sản phẩm này, người dùng sẽ luôn luôn có được trải nghiệm sử dụng tuyệt vời và ổn định nhất.</p>\r\n<p>-Được trang bị giao diện BIOS cực kì thân thiện và dễ sử dụng, việc thao tác cũng như tùy chỉnh đối với ROG RAMPAGE VI EXTREME OMEGA sẽ trở nên cực kì dễ dàng.</p>\r\n<p>-Một trong những đặc điểm nổi bật của ROG RAMPAGE VI EXTREME OMEGA là chiếc màn hình OLED tích hợp, được dùng để hiển thị các thông số hoạt động của hệ thống, hoặc người dùng hoàn toàn có thể thay thế bằng ảnh GIF phù hợp với cá tính của riêng mình.</p>\r\n<p>-Không những vậy, với việc thương hiệu ROG ngày càng được mở rộng, số lượng thiết bị ngày một tăng, giúp người dùng có được nhiều sự lựa chọn tốt hơn, đồng thời đảm bảo tính tương thích tốt nhất về cả hiệu năng lẫn tính thẩm mỹ.</p>\r\n\r\n                                    ', 18440000, 0, '3-Aus-M1-1.png', '[\"3-Aus-M1-3.png\",\"3-Aus-M1-2.png\",\"3-Aus-M1-4.png\"]', 100, 36, 1, 2, 34, 0),
(4, 'Mainboard GIGABYTE B360 M AORUS PRO', 1, 8, '<tr><td> Kích thước </td><td> Micro-ATX</td></tr>\r\n<tr><td> Socket</td><td> LGA 1151-v2 </td> </tr>\r\n<tr><td> Chipset</td><td> B360 </td> </tr>\r\n<tr><td> Khe RAM tối đa</td><td>4 khe</td> </tr>\r\n<tr><td> Hỗ trợ bộ nhớ tối đa</td><td>64GB</td> </tr>\r\n<tr><td> Bus RAM hỗ trợ </td><td>2133MHz ; 2400MHz ; 2666MHz </td> </tr>\r\n<tr><td> Lưu trữ</td><td> 1 x M.2 NVMe ; 1 x M.2 SATA/NVMe ; 6 x SATA 3 6Gb/s ; Hỗ trợ Intel Optane </td> </tr>\r\n<tr> <td> Kiểu RAM hỗ trợ</td><td> DDR4</td> </tr>\r\n<tr> <td> Kiểu khe M.2 hỗ trợ</td><td> M.2 SATA/NVMe</td>  </tr>\r\n<tr> <td> Cổng xuất hình</td> <td>1 x DisplayPort ; 1 x DVI-D ; 1 x HDMI </td> </tr>\r\n<tr><td> Multi-GPU </td><td> AMD CrossFire </td> </tr>\r\n<tr><td> Đèn LED </td><td> RGB </td> </tr>\r\n<tr><td> Khe PCI</td> <td> - 2 x PCIe 3.0 x16<br>- 1 x PCIe 3.0 x4<br>- 1 x PCIe 3.0 x1 </td> </tr>\r\n<tr><td> Số cổng USB </td><td> - 1 x USB Type-C <br>- 3 x USB 3.1 (tối đa 5)<br>- 2 x USB 2.0 (tối đa 6) </td> </tr>\r\n<tr><td> LAN</td><td>  LAN 1 Gb/s </td> </tr>\r\n<tr><td> Âm thanh </td><td> Realtek ALC892 codec 7.1-channel </td> </tr>\r\n', '<p>-Bộ nâng cấp WIFI Intel CNVi: Bộ nâng cấp WIFI Intel CNVi cung cấp một cách dễ dàng để nâng cấp bo mạch chủ Intel CNVi WIFI mới với kết nối Gigabit WIFI mới và nhanh hơn mà không phải nâng cấp lên toàn bộ hệ thống mới.</p>\r\n<p>-Thiết kế khe cắm M.2 chế độ kép Hỗ trợ cả SSD PCIe NVMe + SATA: Bo mạch chủ AORUS Gaming được xây dựng với các khe cắm M.2 kép. Với thiết kế chế độ kép PCIe NVMe + SATA, người dùng có thể xây dựng hệ thống lưu trữ RAID với hai ổ SSD NVMe M.2 hoặc SSD M.2 SATA + SSD SATA. Cấu hình này được thiết kế để linh hoạt và thích ứng để phù hợp với hầu hết các yêu cầu của người dùng và game thủ.</p>\r\n<p>-Hiệu suất tối ưu với Optane: Nền tảng AORUS B360 hỗ trợ công nghệ Optane mới nhất của Intel. Optane hoạt động như một ổ đĩa hỗ trợ dung lượng đệm lớn cho ổ cứng giúp tăng tốc độ đọc đáng kể so với các ổ đĩa cơ truyền thống.</p>\r\n<p>-Intel ® GbE LAN với Mạng tăng tốc Internet cFosSpeed: Intel ® GbE LAN có tính năng cFosSpeed, một ứng dụng quản lý lưu lượng mạng giúp cải thiện độ trễ mạng và duy trì độ trễ thấp để mang lại khả năng đáp ứng tốt hơn trong môi trường LAN băng thông sử dụng dày đặc.</p>\r\n<p>-LAN chống tĩnh điện & chống sét hàng đầu trong ngành: Các bo mạch chủ của GIGABYTE sử dụng các IC có mức kháng lên tới 3 lần so với các IC truyền thống. Điều này giúp bảo vệ tốt hơn bo mạch chủ và các thành phần của nó chống lại thiệt hại tiềm ẩn do tĩnh điện gây ra. Các bo mạch chủ của GIGABYTE cũng có các IC chống shock điện, giúp đảm bảo rằng PC của bạn được trang bị để đối phó với mọi khả năng xảy ra từ dòng điện không ổn định.</p>\r\n<p>-Thiết kế tản nhiệt tiên tiến: </p>\r\n<ul><li>Lớp vỏ tản nhiệt cho M.2 SSD: Thiết kế vỏ bảo vệ và phủ một đệm tản nhiệt bên trong luôn giữ cho M.2 SSD hoạt động ở nhiệt độ thấp.</li>\r\n<li>Tản nhiệt MOSFET: Thiết kế vết cắt trên bề mặt cải thiện hiệu suất nhiệt bằng cách tăng bề mặt tản nhiệt và duy trì tính thẩm mỹ cho mainboard.</li>\r\n<li>Đệm tản nhiệt tăng độ dày: Cải thiện độ dày của miếng đệm nhiệt lên 1,5mm, giúp tản nhiệt vượt trội cho MOSFET.</li>\r\n<p>-Khe PCI-e bọc thép siêu bền: Thiết kế chắc chắn bằng thép không gỉ từ GIGABYTE củng cố các chân kết nối PCI-e để cung cấp thêm sức mạnh cần thiết để hỗ trợ các card đồ họa nặng không bị cong, xệ.</p>\r\n<p>-Bảo vệ bo mạch khỏi sự ảnh hưởng từ Lưu huỳnh: Các hợp chất lưu huỳnh trong không khí có thể xuyên qua các điện trở nhỏ trên bo mạch tạo ra sự thay đổi hóa học và làm cho các điện trở này hở hoặc ngắn mạch. Nếu một trong hai điều này xảy ra bo mạch chủ sẽ không hoạt động. Bằng cách trang bị điện trở với thiết kế chống lưu huỳnh, GIGABYTE mang đến cho bo mạch chủ siêu bền với một ý nghĩa hoàn toàn mới.</p>\r\n\r\n\r\n                                    ', 2250000, 0, '4-Giga-M1-1.jpg', '[\"4-Giga-M1-3.jpg\",\"4-Giga-M1-2.jpg\",\"4-Giga-M1-4.jpg\"]', 100, 36, 1, 3, 54, 0),
(5, 'Mainboard GIGABYTE H310M DS2', 1, 8, '<tr><td> Kích thước </td><td> Micro-ATX</td></tr>\r\n<tr><td> Socket</td><td> LGA 1151-v2 </td> </tr>\r\n<tr><td> Chipset</td><td> H310 </td> </tr>\r\n<tr><td> Khe RAM tối đa</td><td>2 khe</td> </tr>\r\n<tr><td> Hỗ trợ bộ nhớ tối đa</td><td>32GB</td> </tr>\r\n<tr><td> Bus RAM hỗ trợ </td><td>2133MHz ; 2400MHz ; 2666MHz </td> </tr>\r\n<tr><td> Lưu trữ</td><td> 4 x SATA 3 6Gb/s </td> </tr>\r\n<tr> <td> Kiểu RAM hỗ trợ</td><td> DDR4</td> </tr>\r\n<tr> <td> Cổng xuất hình</td> <td>1 x VGA/D-sub </td> </tr>\r\n<tr><td> Đèn LED </td><td> Đơn sắc </td> </tr>\r\n<tr><td> Khe PCI</td> <td> - 1 x PCI Express x16<br>- 2 x PCI Express x1 </td> </tr>\r\n<tr><td> Số cổng USB </td><td> - 2 x USB 3.1 (tối đa 4)<br>- 4 x USB 2.0 (tối đa 6) </td> </tr>\r\n<tr><td> LAN</td><td> 1 x LAN 1 Gb/s </td> </tr>\r\n<tr><td> Âm thanh </td><td> Realtek ALC887 codec 7.1-channel </td> </tr>\r\n', '<p>Công nghệ nổi bật có trên Bo mạch chủ Gigabyte H310M-DS2:</p>\r\n<p>Mạng LAN Gigabit có tốc độ cực cao và khả năng phân bổ tự động với chip mạng Realtek 8118 LAN (là một chip mạng hiệu suất cao và thân thiện cho người chơi game với phân bổ băng thông tự động để đảm bảo mức độ ưu tiên mạng cao nhất của trò chơi hoặc ứng dụng. Nó có thể cung cấp cho người dùng các tính năng toàn diện nhất và trải nghiệm Internet nhanh nhất và mượt mà nhất.)</p>\r\n<p>Hỗ trợ 5 quạt cho hiệu suất tản nhiệt cao, công nghệ Fan Stop cho phép dừng quạt khi nhiệt độ của hệ thống ở dưới ngưỡng quy định, và quạt sẽ hoạt động với vòng quay tương ứng với mỗi mức nhiệt độ khi nhiệt độ lên cao.</p>\r\n<p>Hỗ trợ tốt thực tế ảo với hệ thống khuyến khích đi kèm từ Core i5 , Nvidia 1060 6G và 8G RAM.       </p>                             ', 2250000, 0, '5-Giga-M1-1.png', '[\"5-Giga-M1-2.png\",\"5-Giga-M1-3.png\",\"5-Giga-M1-4.png\"]', 100, 36, 1, 4, 12, 0),
(236, 'Mainboard MSI B450M BAZOOKA PLUS', 1, 27, '                                        <tr><td> Kích thước </td><td> Micro-ATX</td></tr>\r\n<tr><td> Socket</td><td> AM4 </td> </tr>\r\n<tr><td> Chipset</td><td> B450</td> </tr>\r\n<tr><td> Khe RAM tối đa</td><td>4 khe</td> </tr>\r\n<tr><td> Hỗ trợ bộ nhớ tối đa</td><td>64GB</td> </tr>\r\n<tr><td> Bus RAM hỗ trợ </td><td>1866MHz ; 2133MHz ; 2400MHz ; 2666MHz ; 2800MHz ; 2933MHz ; 3000MHz ; 3066MHz ; 3200MHz ; 3333MHz </td> </tr>\r\n<tr><td> Lưu trữ</td><td> 1 x M.2 SATA/NVMe ; 4 x SATA 3 6Gb/s </td> </tr>\r\n<tr> <td> Kiểu RAM hỗ trợ</td><td> DDR4</td> </tr>\r\n<tr> <td> Kiểu khe M.2 hỗ trợ</td><td> M.2 SATA/NVMe</td>  </tr>\r\n<tr> <td> Cổng xuất hình</td> <td>1 x DVI-D ; 1 x HDMI </td> </tr>\r\n<tr><td> Đèn LED </td><td> RGB </td> </tr>\r\n<tr><td> Khe PCI</td> <td> -1 x PCIe 3.0 x16<br>-2 x PCIe 2.0 x1 </td> </tr>\r\n<tr><td> Số cổng USB </td><td> - 4 x USB 3.1 (tối đa 8) <br>- 2 x USB 2.0 (tối đa 6)</td> </tr>\r\n<tr><td> LAN</td><td> 1 x LAN 1 Gb/s </td> </tr>\r\n<tr><td> Âm thanh </td><td> Realtek® ALC892 7.1-Channel </td> </tr>\r\n                                    ', '                                        <p>Hỗ trợ AMD® Ryzen ™ Thế hệ 1 và 2 / Ryzen ™ với Bộ xử lý đồ họa Radeon ™ Vega cho Socket AM4</p>\r\n\r\n<p>Hỗ trợ bộ nhớ DDR4, lên đến 3333+ (OC) MHz. Hỗ trợ bộ nhớ ECC UDIMM.</p>\r\n\r\n<p>Trải nghiệm tải tốc độ cao: 1 cổng TURBO M.2, công nghệ Store MI.</p>\r\n\r\n<p>Thiết kế tản nhiệt mở rộng: MSI mở rộng PWM và thiết kế mạch nâng cao đảm bảo ngay cả các bộ vi xử lý cao cấp cũng chạy ở tốc độ tối đa.</p>\r\n\r\n<p>Core Boost: Với bố cục cao cấp và thiết kế nguồn hoàn toàn kỹ thuật số để hỗ trợ nhiều lõi hơn và cung cấp hiệu suất tốt hơn.</p>\r\n\r\n<p>DDR4 Boost: Công nghệ tiên tiến để cung cấp tín hiệu dữ liệu thuần cho hiệu năng và độ ổn định chơi game tốt nhất.</p>\r\n\r\n<p>Audio Boost: Thưởng thức chất lượng âm thanh cấp phòng thu cho trải nghiệm âm thanh sống động nhất.</p>\r\n\r\n<p>Mystic Light và đồng bộ Mystic Light: 16,8 triệu màu / 10 hiệu ứng trong một cú nhấp chuột. Đồng bộ hóa dải led RGB và các giải pháp RGB khác để tùy chỉnh.</p>\r\n\r\n<p>BIOS FLASHBACK +: Chỉ cần sử dụng phím USB để flash bất kỳ BIOS nào trong vài giây mà không cần cài đặt CPU, bộ nhớ hoặc card đồ họa.</p>\r\n\r\n<p>Khe PCI-E bọc thép: Bảo vệ card đồ hoạ khỏi cong xệ.</p>\r\n\r\n<p>VR Ready: Trải nghiệm trò chơi thực tế ảo tốt nhất không có độ trễ, giảm chóng mặt.</p>                                                                                                                                                                                                    ', 2400000, 1000, '6-MSI-M1-1.jpg', '[\"6-MSI-M1-2.jpg\",\"6-MSI-M1-3.jpg\",\"6-MSI-M1-4.jpg\"]', 100, 36, 1, 5, 26, 3.75),
(239, 'Mainboard MSI A320M PRO-M2 V2', 1, 27, '<tr><td> Kích thước </td><td> Micro-ATX</td></tr>\r\n<tr><td> Socket</td><td> AM4 </td> </tr>\r\n<tr><td> Chipset</td><td> A320</td> </tr>\r\n<tr><td> Khe RAM tối đa</td><td>2 khe</td> </tr>\r\n<tr><td> Hỗ trợ bộ nhớ tối đa</td><td>32GB</td> </tr>\r\n<tr><td> Bus RAM hỗ trợ </td><td>1866MHz ; 2133MHz ; 2400MHz ; 2666MHz ; 2933MHz ; 3200MHz </td> </tr>\r\n<tr><td> Lưu trữ</td><td> M.2 SATA/NVMe </td> </tr>\r\n<tr> <td> Kiểu RAM hỗ trợ</td><td> DDR4</td> </tr>\r\n<tr> <td> Kiểu khe M.2 hỗ trợ</td><td> M.2 SATA/NVMe</td>  </tr>\r\n<tr> <td> Cổng xuất hình</td> <td>1 x DVI-D ; 1 x HDMI ; 1 x VGA/D-sub </td> </tr>\r\n<tr><td> Khe PCI</td> <td> -1 x PCIe 3.0 x16<br>-2 x PCIe 2.0 x1 </td> </tr>\r\n<tr><td> Số cổng USB </td><td>-4 x USB 3.1 (tối đa 6)<br>-2 x USB 2.0 (tối đa 6) </td> </tr>\r\n<tr><td> LAN</td><td> LAN 1 Gb/s </td> </tr>\r\n<tr><td> Âm thanh </td><td> Realtek® ALC887 Codec </td> </tr>\r\n', '<p>A320M PRO-M2 V2 là chiếc mainboard phổ thông của MSI, hỗ trợ các dòng CPU Ryzen thế hệ mới nhất đồng thời được trang bị đầy đủ tính năng sử dụng cho bất kì cấu hình tầm trung nào đi kèm với thiết kế nhỏ gọn.</p>\r\n<p>Mặc dù chỉ là 1 chiếc mainboard phổ thông nhưng toàn bộ linh kiện trên MSI A320M PRO-M2 V2 đều là linh kiện cao cấp được gia công kĩ lưỡng nhằm đem lại trải nghiệm tốt nhất cho người sử dụng đồng thời kéo dài tuổi thọ hoạt động.</p>\r\n\r\n<p>Với thiết kế mạch tối ưu, hỗ trợ người dùng có thể ép xung bộ nhớ lên tới tốc độ 3200MHz, giúp tận dụng tối đa sức mạnh của các bộ xử lý Ryzen thế hệ mới nhất.</p>\r\n\r\n<p>Về lưu trữ, trên mainboard là khe cắm M.2 được tích hợp sẵn với tốc độ lên tới 32Gb/s, sẵn sàng hỗ trợ lưu trữ tốc độ cao khi có yêu cầu sử dụng của người dùng.</p>\r\n\r\n<p>Mặc dù không có hệ thống tản nhiệt hầm hố như những chiếc mainboard cao cấp hơn, nhưng với thiết kế tốt và hiệu suất hoạt động cao, MSI A320M PRO-M2 V2 có nhiệt độ hoạt động rất tốt, chưa kể tới 2 chân cắm 4pin được tích hợp sẵn giúp cho người dùng điều chỉnh tốc độ quay của quạt để phù hợp với nhiệt độ hoạt động.</p>\r\n\r\n<p>Sử dụng bộ giải mã âm thanh ALC887 và mạch xử lý âm thanh được tách biệt hoàn toàn ra khỏi phần còn lại trên mainboard, giúp đem lại tín hiệu âm thanh tốt nhất tới người dùng.</p>\r\n\r\n<p>Đi kèm với MSI A320M PRO-M2 V2 là giao diện BIOS thân thiện với 2 chế độ Ez-mode và Advanced-mode, giúp đáp ứng nhu cầu sử dụng cơ bản lẫn nâng cao của người dùng.</p>\r\n\r\n                                    ', 1390000, 0, '7-MSI-M1-1.jpg', '[\"7-MSI-M1-2.jpg\",\"7-MSI-M1-3.jpg\",\"7-MSI-M1-4.jpg\"]', 100, 36, 1, 6, 22, 0),
(240, 'Mainboard ASRock H310CM-DVS', 1, 28, '                                        <tr><td> Kích thước </td><td> Micro-ATX </td></tr>\r\n<tr><td> Socket</td><td> LGA 1151-v2 </td> </tr>\r\n<tr><td> Chipset</td><td> H310 </td> </tr>\r\n<tr><td> Khe RAM tối đa</td><td>2 khe</td> </tr>\r\n<tr><td> Hỗ trợ bộ nhớ tối đa</td><td>32GB</td> </tr>\r\n<tr><td> Bus RAM hỗ trợ </td><td>2133MHz ; 2400MHz ; 2666MHz </td> </tr>\r\n<tr><td> Lưu trữ</td><td>4 x SATA 3 6Gb/s </td> </tr>\r\n<tr> <td> Kiểu RAM hỗ trợ</td><td> DDR4</td> </tr>\r\n<tr> <td> Cổng xuất hình</td> <td>1 x DVI-D ; 1 x VGA/D-sub </td> </tr>\r\n<tr><td> Khe PCI</td> <td>- 1 x PCI 3.0 x16<br>- 1 x PCI 2.0 x1 </td> </tr>\r\n<tr><td> Số cổng USB </td><td>- 2 x USB 3.1 (tối đa 4)</td> </tr>\r\n<tr><td> LAN</td><td> LAN 1 Gb/s </td> </tr>\r\n<tr><td> Âm thanh </td><td> 7.1 CH HD Audio (Realtek ALC887 Audio Codec)</td> </tr>\r\n                                    ', '                                        <p>Giải mã / mã hóa 10 bit HEVC: Hỗ trợ Giải mã / Mã hóa 10 bit HEVC để cung cấp chất lượng video được cải thiện đáng kể và trải nghiệm video có độ chi tiết hình ảnh cao hơn.</p>\r\n\r\n<p>\"Super Alloy motherboard\": Bo mạch có màu đen toàn phần rắn chắc nhờ thiết kế Sapphire Black và cho ngoại hình bí ẩn. Cùng với chất liệu lưới sợi thuỷ tinh bảo vệ các mạch điện bên dưới khỏi độ ẩm môi trường.</p>\r\n\r\n<p>Thiết kế tụ điện rắn: ASRock áp dụng tụ rắn trên bo mạch chủ Asrock H310CM-DVS. Với các nắp cứng, bo mạch chủ này được nâng cao chất lượng, hoạt động ổn định hơn và đảm bảo tuổi thọ lâu hơn.</p>\r\n\r\n<p>Tụ âm thanh ELNA: So với các tụ truyền thống, độ rò của tụ âm thanh ELNA chỉ là 3uA. Đây là lý do mà độ nhiễu âm thanh được giảm đáng kể và thoả mãn cả những \"audiophile khó tính nhất\".</p>\r\n\r\n<p>Chân cắm hỗ trợ tản nước: Đây không chỉ là một chân cắm quạt CPU tiêu chuẩn, mà còn hỗ trợ máy bơm của hệ thống nước. Chân cắm này cung cấp công suất tối đa 1,5A để hỗ trợ các tản nước phổ biến nhất. Người dùng cũng có thể điều chỉnh điện áp để có hiệu suất làm mát cao hơn với độ ồn thấp hơn.</p>\r\n<p>A-Tuning: A-Tuning là bộ phần mềm đa năng của ASRock với giao diện mới, nhiều tính năng mới và các tiện ích cải tiến.</p>\r\n\r\n<p>Bảo vệ khỏi quá tải: Một số thành phần kỹ thuật số nhạy cảm trên bo mạch chủ dễ bị quá tải điện, dòng điện quá mức có thể khiến hệ thống của bạn gặp trục trặc ngay lập tức. ASRock Full Spike Protection bao gồm các công nghệ khác nhau ngăn các thành phần bo mạch chủ của bạn bị hỏng do các xung điện áp không mong muốn này.</p>\r\n                                                                        ', 1440000, 1000, '8-ASR-M1-1.jpg', '[\"8-ASR-M1-2.jpg\",\"8-ASR-M1-3.jpg\",\"8-ASR-M1-4.jpg\"]', 100, 36, 1, 7, 11, 0),
(241, 'Mainboard ASRock Z390 Taichi', 1, 28, '                                        <tr><td> Kích thước </td><td> ATX</td></tr>\r\n<tr><td> Socket</td><td> LGA 1151-v2 </td> </tr>\r\n<tr><td> Chipset</td><td> Z390</td> </tr>\r\n<tr><td> Khe RAM tối đa</td><td>4 khe</td> </tr>\r\n<tr><td> Hỗ trợ bộ nhớ tối đa</td><td>64GB</td> </tr>\r\n<tr><td> Bus RAM hỗ trợ </td><td> 2133MHz ; 2400MHz ; 2666MHz ; 2800MHz ; 2933MHz ; 3200MHz ; 3600MHz ; 3733MHz ; 3800MHz ; 3866MHz ; 4000MHz ; 4133MHz ; 4200MHz</td> </tr>\r\n<tr><td> Lưu trữ</td><td>3 x M.2 SATA/NVMe ; 8 x SATA 3 6Gb/s ; Hỗ trợ Intel Optane</td> </tr>\r\n<tr> <td> Kiểu RAM hỗ trợ</td><td> DDR4</td> </tr>\r\n<tr> <td> Kiểu khe M.2 hỗ trợ</td><td> M.2 SATA/NVMe</td>  </tr>\r\n<tr> <td> Cổng xuất hình</td> <td>1 x DisplayPort ; 1 x HDMI</td> </tr>\r\n<tr><td> Khe PCI</td> <td>- 3 x PCIe 3.0 x16<br>- 2 x PCIe 3.0 x1</td> </tr>\r\n<tr><td> Multi-GPU </td><td> NVIDIA SLI </td> </tr>\r\n<tr><td> Đèn LED </td><td> RGB </td> </tr>\r\n<tr><td> Số cổng USB </td><td>- 1 x USB Type C (tối đa 2)<br>- 7 x USB 3.1 (tối đa 11)<br>- mở rộng tối đa 3 x USB 2.0 </td> </tr>\r\n<tr><td> LAN</td><td> 1 x LAN 1 Gb/s </td> </tr>\r\n<tr><td> Âm thanh </td><td> Realtek® ALC1220 7.1-Channel </td> </tr>\r\n                                    ', '                                        <p>Z390 Taichi là mẫu bo mạch chủ cao cấp nhất hiện nay của ASRock trên socket 1151, hỗ trợ các dòng CPU thế hệ thứ 9 của Intel với rất nhiều tính năng cao cấp như USB Type C, SLI, 2 bộ xử lý mạng độc lập, kết nối WiFi..... cùng với thiết kế tối ưu cho việc ép xung.</p>\r\n\r\n<p>Bộ điều khiển cấp nguồn: ASRock Z390 Taichi sử dụng bộ cấp nguồn cao cấp với hiệu suất cao, đảm bảo đem lại khả năng hoạt động ổn định trong khi chơi game, sử dụng các tác vụ nặng và đặc biệt là ép xung.</p>\r\n\r\n<p>ASRock Z390 Taichi sử dụng bộ cấp nguồn cho CPU gồm 12 \"phase\" tất cả, đem lại dòng điện cực kì ổn định cho CPU, đồng thời có hiệu suất rất cao nhằm đạt được độ ổn định tối đa trong khi ép xung.</p>\r\n\r\n<p>Để đạt được điều này, ASRock Z390 Taichi được ASRock đầu tư thiết kế rất chau chuốt, với hệ thống tản nhiệt lớn, cuộn cảm cấp nguồn cho CPU có khả năng tải dòng điện lên tới 60A hay tụ Nichicon cao cấp với tuổi thọ lên tới hơn 12000 giờ liên tục.</p>\r\n<p>Khe cắm PCIe được gia cố vững chắc: Toàn bộ các khe cắm PCIe chính trên ASRock Z390 Taichi đều được bọc bên ngoài bằng phần khung thép giúp tăng khả năng chịu lực đối với những dòng đồ họa cao cấp có khối lượng lớn.</p>\r\n<p>Hiệu năng: ASRock Z390 Taichi được trang bị 3 khe cắm M.2 hỗ trợ chuẩn kết nối PCIe 3.0 với băng thông lên tới 32Gb/s đem lại tốc độ truy cập dữ liệu nhanh nhất cho game thủ. Đồng thời ASRock Z390 Taichi còn đi kèm với lớp tản nhiệt cho khe cắm M.2, giúp cho thiết bị lưu trữ M.2 SSD luôn hoạt động ở điều kiện tốt nhất.</p>\r\n\r\n<p>Ngoài ra, ASRock Z390 Taichi còn hỗ trợ bộ nhớ Intel Optane giúp tăng tốc độ truy xuất dữ liệu của ổ cứng truyền thống.</p>\r\n\r\n<p>Bộ xử lý mạng: ASRock Z390 Taichi sử dụng đồng thời cùng lúc 2 bộ xử lý mạng là I219V và I211AT của Intel, hỗ trợ tính năng Teaming cho phép gộp 2 kết nối lại làm một giúp tăng băng thông mạng lên gấp 2 lần.</p>\r\n\r\n<p>Ngoài ra ASRock Z390 Taichi còn được trang bị kết nối WiFi với chuẩn kết nối 802.11ac có băng thông lên tới 433Mbps và Bluetooth 4.2, hoàn toàn đủ sức đáp ứng nhu cầu kết nối không dây tốc độ cao của game thủ.</p>\r\n\r\n<p>Tùy biến: Không như nhiều dòng bo mạch chủ khác, ASRock Z390 Taichi có hệ thống ánh sáng RGB khá đơn giản, chỉ tập trung vào những điểm nhấn trên thiết kế của mình đồng thời hỗ trợ game thủ tùy biến cũng như đồng bộ ánh sáng với các thiết bị khác được phần mềm Polychrome của ASRock hỗ trợ.</p>\r\n\r\n<p>Nếu như bạn đang cần một chiếc bo mạch chủ \"flag ship\", sở hữu nhiều tính năng cao cấp với khả năng hỗ trợ ép xung rất tốt và thiết kế đặc biệt, cứng cáp nhưng lại có một mức giá phù hợp thì ASRock Z390 Taichi là một lựa chọn cực kì hợp lý trong phân khúc mà bạn không nên bỏ qua.</p>\r\n\r\n                                                                        ', 6200000, 10000, '9-ASR-M1-1.jpg', '[\"9-ASR-M1-2.jpg\",\"9-ASR-M1-3.jpg\",\"9-ASR-M1-4.jpg\"]', 100, 36, 1, 8, 25, 0),
(245, 'CPU AMD Ryzen 5 2400G', 2, 29, '                                        <tr><td>CPU</td><td> Ryzen 5 2400G</td></tr>\r\n <tr><td> Socket</td><td> AM4 </td> </tr>\r\n<tr><td> Kiến trúc </td><td> Zen+ (12 nm)</td> </tr>\r\n<tr><td> Code name</td><td> Raven Ridge</td> </tr>\r\n<tr><td> Thế hệ</td><td> AMD Ryzen thế hệ thứ 2</td> </tr>\r\n<tr><td> Dòng</td><td> Ryzen 5</td> </tr>\r\n<tr> <td> Tốc độ xử lý</td><td>3.6 GHz - 3.9 GHz</td> </tr>\r\n<tr> <td> Số nhân xử lý</td><td>4</td>  </tr>\r\n<tr> <td> Số luồng xử lý</td> <td>8</td> </tr>\r\n<tr><td> Cache</td><td>4MB</td> </tr>\r\n<tr><td> Chip đồ họa </td><td> AMD Vega 11 Graphics</td> </tr>\r\n<tr><td> TDP</td><td>65W</td> </tr>\r\n<tr><td> Hyper-Threading </td><td>Có</td> </tr>\r\n<tr><td> Bộ nhớ hỗ trợ</td><td> DDR4 Dual channel</td> </tr>\r\n                                    ', '                                        <p>Raven Ridge APU là 1 dòng sản phẩm khá đặc biệt, với tình trạng giá thành đắt đỏ của linh kiện ở thời điểm hiện tại. Việc ráp 1 bộ máy để chơi game thực sự trở nên rất nan giải trong thời gian hơn 1 năm trở lại đây, nên việc các dòng APU như Ryzen 3 2200G và Ryzen 5 2400G được tung ra thị trường được xem là những chiếc phao cứu sinh cho những game thủ có kinh phí hạn hẹp đang tìm 1 chiếc máy tính có khả năng chơi game tương đối tốt và dễ dàng nâng cấp về sau này. Trong bài viết này sẽ đánh giá tổng quát hiệu năng của 2 dòng APU trên kiến trúc Zen+ mới nhất của AMD này, để xem liệu việc lựa chọn APU hay dòng card màn hình giá rẻ như GT 1030 thì lựa chọn nào sẽ trở nên phù hợp hơn.</p>\r\n<p>OVERWATCH: Trên tựa game Overwatch với thiết lập Medium ở độ phân giải 1080p, có thể thấy ở đây G4560 khi sử dụng với RAMcó xung nhịp 2400Mhz và GT 1030 cho mức khung hình trung bình là 63FPS khá tương đồng với R3 1200 (3.9GHz) đi với RAM có xung nhịp 2933MHz và GT 1030 có mức khung hình 61FPS. Trong khi đó R5 2400G sau đi được ép xung CPU lên 3.95GHz và 1.6GHz đối với iGPU (chip đồ họa tích hợp sẵn) cho hiệu năng khá sát ở phía sau với khung hình trung bình là 60FPS.</p>\r\n<p>Ở xung nhịp mặc định khi ghép R5 2400G với cặp RAM G.Skill Trident Z (3200MHz CL14) đem lại khung hình trung bình xấp xỉ ở mức 54FPS, thấp hơn ~11% so với sau khi được ép xung. R3 2200G cũng không hề kém cạnh, khi mà mức khung hình trung bình cũng chỉ thấp hơn 8% so với R5 2400G ở xung nhịp mặc định, còn sau khi được ép xung CPU lên 3.9GHz và 1.65GHz đối với iGPU, hiệu năng của R3 2200G vẫn tiếp tục duy trì khoảng cách ~9% so với R5 2400G. Mặc dù là hiệu năng trên Overwatch của R5 2400G có hơn ~10% so với R3 2200 nhưng xét trên sự chênh lệch về giá thành thì sự chênh lệch này là ko quá đáng kể.</p>\r\n\r\n<p>ROCKET LEAGUE: Tiếp đến ở tựa game Rocket League, với thiết lập kết hợp giữa Medium và High trên độ phân giải 1080p, ở đây sau khi được ép xung R5 2400G vẫn tiếp tục duy trì khung hình trung bình khá tốt ở mức 60FPS tương đương với G4560 và R3 1200 (3.9GHz) với GT 1030, theo ngay sát phía sau là R3 2200G. Trong khi đó ở xung nhịp mặc định, R3 2200G vẫn đạt được mức khung hình trung bình là 54FPS chỉ thấp hơn ~7% so với R5 2400G, 1 lần nữa cho thấy sự cải thiện về hiệu năng chơi game trên R5 2400G là không quá nhiều so với R3 2200G.</p>\r\n<p>Tuy nhiên có 1 vấn đề về Ryzen 2 là việc 0.1% frame time trên cả R3 2200G và R5 2400G gây nên hiện tượng sụt khung hình ở 1 số thời điểm nhất định, mặc dù chỉ với 0.1% frame time thấp thì hiện này là gần như không đáng kể. Nguyên nhân phần lớn có thể là do driver hoặc tựa game Rocket League chưa tung ra bản cập nhật cho kiến trúc Zen+ mới vì hiện tượng này hoàn toàn không xảy ra đối với tựa game Overwatch.</p>\r\n\r\n<p>SNIPER ELITE 4: Ở tựa game này thì phần cứng của AMD được ưu ái hơn hẳn so với đối thủ Intel của mình, với thiếp lập High trên độ phân giải 1080p, R5 2400G đem lại hiệu năng vượt trội hơn hẳn (~29%) so với G4560 và GT 1030 sau khi được ép xung.</p>\r\n\r\n<p>CIVILIZATION V: Đối với 1 tựa game có yêu cầu tương đối cao về CPU như Civilization V, việc so sánh hiệu năng cũng có khác biệt so với những tựa game khác, khi mà ở đây sẽ lấy thời gian điều khiển A.I trong game làm thước đo để so sánh hiệu năng thay vì chỉ dùng FPS để so sánh. Tất cả các dòng CPU không có ghi chú gì theo sau thì đều được ghép với bộ RAM có tốc độ là 3200MHz, bạn có thể thấy rõ được phần CPU của R5 2400G (3.95GHz) có hiệu năng xấp xỉ so với R5 1600X với thời gian là 19.5 giây. Với số lượng người chơi lên tới 5 người thì thời gian này sẽ tăng lên thành 1.6 phút. Trong khi đó, R3 2200G sau khi được ép xung (24.7 giây) có hiệu năng cao hơn ~22% so với xung nhịp gốc (27.5 giây).</p>\r\n\r\n<p>CS: GO: CS: GO là 1 tựa game có thể nói là khá nhạy cảm so với việc thay đổi RAM, với thiết lập tùy chỉnh trên độ phân giải 1080p, ở xung nhịp mặc định R5 2400G đạt được 95FPS trung bình, theo sau là R3 2200G với 86FPS, trong khi đó G4560 và GT 1030 dẫn trước khá xa với 120FPS. Sau khi được ép xung thì mức khung hình của cả R5 2400G và R3 2200G cũng được tăng lên khá đáng kể, nhưng vẫn thấp hơn ~20% so với G4560 và GT 1030, tuy nhiên sự khác biệt giữa R3 2200G và R5 2400G ở đây là rất nhỏ.</p>\r\n\r\n<p>DOTA 2: Chuyển sang DOTA 2 với thiết lập High trên 1080p, khi ghép với GT 1030 thì cả R3 1200 (3.9GHz) và G4560 đều đạt được 63FPS trung bình, trong khi đó cả R3 2200G và R5 2400G đều có hiệu năng tương đồng với nhau sau khi được xung.</p>\r\n<p>Với xung nhịp mặc định thì kết quả cũng tương tự như vậy với mức khung hình xấp xỉ ở mức 40-45FPS tùy thuộc vào tốc độ và độ trễ của các dòng RAM.</p>\r\n\r\n<p>TOTAL WAR: WARHAMMER: Cuối cùng ở tựa game Total Warhammer, với thiết lập Medium trên 1080p, ở xung nhịp mặc định hiệu năng của R5 2400 khá tương đồng với G4560 và GT 1030 theo sau là R3 2200G với hiệu năng thấp hơn ~13%. Tuy nhiên sau khi được ép xung lên thì R5 2400G vươn lên vị trí đầu bảng là 43FPS trung bình, theo sau là R3 2200G với 39FPS cao hơn ~11-22% so với G4560 đi với GT 1030.</p>\r\n\r\n<p>Nhiệt độ và công suất tiêu thụ: Với việc được tích hợp 1 vi xử lý đồ họa mạnh mẽ (iGPU) nhưng nhiệt độ và công suất tiêu thụ của R3 2200G và R5 2400G là khá tốt với mức TDP chỉ ở 65W, với chiếc tản nhiệt được AMD tặng kèm thì nhiệt độ của R3 2200G sau khi được ép xung (1.4v) vẫn khá tốt (69 độ C) trong quá trình stress test bằng Prime95. Chuyển sang 1 bộ tản nhiệt nước AIO như Kraken X62, giúp cải thiện nhiệt độ hoạt động của R3 2200G xuống đáng kể, chỉ còn 47 độ C.</p>\r\n<p>Trong khi đó về công suất tiêu thụ thì R3 2200G thể hiện khá xuất sắc với 1 việc được tích hợp sẵn 1 iGPU mạnh mẽ như vậy nhưng công suất tiêu thụ thực tế chỉ ở 72W với xung nhịp mặc định và 82W sau khi được ép xung (1.4v).</p>\r\n<p>Kết: Qua các kết quả so sánh, không còn nghi ngờ gì nữa khi nói rằng R3 2200G là 1 lựa chọn có hiệu năng trên giá thành tốt nhất trong phân khúc này ở thời điểm hiện tại, với phần hiệu năng của CPU đạt 78-85% so với R5 2400G với mức giá chỉ gần bằng 1 nửa. Trong khi đó phần iGPU lại không có quá nhiều sự khác biệt giữa 2 dòng APU này, khi mà iGPU của R3 2200G vẫn đủ khả năng để xử lý tốt các tựa game e-Sport ở thiết lập Medium/High trên độ phân giải 1080p.</p>\r\n<p>Mặc dù hiệu năng chơi game của R5 2400G khá tương đồng so với việc sử dụng G4560 và GT 1030 nhưng việc sử dụng APU của AMD sẽ đòi hỏi bạn phải sử dụng các bộ RAM có tốc độ rất nhanh để đạt được hiệu năng mong muốn, cộng thêm tình trạng giá thành RAM hiện nay việc này có thể khiến cho chi phí cao hơn so với G4560 và GT 1030. Cho nên trừ khi bạn có nhu cầu sử dụng những ứng dụng có yêu cầu cao về khả năng xử lý của CPU, thì R3 2200G sẽ là 1 lựa chọn phù hợp hơn rất nhiều đối với 1 bộ máy tính có chi phí thấp.</p>\r\n<p>Hi vọng bài viết này sẽ giúp cho bạn có sự lựa chọn chính xác hơn và dễ dàng hơn cho nhu cầu của mình.    </p>                                                                    ', 4390000, 0, '10-AMD-C2-11.png', '[\"10-AMD-C2-31.jpg\",\"10-AMD-C2-21.png\",\"10-AMD-C2-12.png\"]', 100, 36, 1, 9, 23, 0),
(246, 'CPU AMD Ryzen 3 2200G', 2, 29, '                                                                                <tr><td>CPU</td><td> Ryzen 3 2200G </td></tr>\r\n <tr><td> Socket</td><td> AM4 </td> </tr>\r\n<tr><td> Kiến trúc </td><td> Zen (14 nm)</td> </tr>\r\n<tr><td> Code name</td><td> Raven Ridge</td> </tr>\r\n<tr><td> Thế hệ</td><td> AMD Ryzen thế hệ thứ 2</td> </tr>\r\n<tr><td> Dòng</td><td> Ryzen 3</td> </tr>\r\n<tr> <td> Tốc độ xử lý</td><td>3.5 GHz - 3.7 GHz</td> </tr>\r\n<tr> <td> Số nhân xử lý</td><td>4</td>  </tr>\r\n<tr> <td> Số luồng xử lý</td> <td>4</td> </tr>\r\n<tr><td> Cache</td><td>4MB</td> </tr>\r\n<tr><td> Chip đồ họa </td><td> AMD Vega 8 Graphics</td> </tr>\r\n<tr><td> TDP</td><td>65W</td> </tr>\r\n<tr><td> Bộ nhớ hỗ trợ</td><td> DDR4 Dual channel</td> </tr>\r\n                                                                        ', '                                                                                <p>Raven Ridge APU là 1 dòng sản phẩm khá đặc biệt, với tình trạng giá thành đắt đỏ của linh kiện ở thời điểm hiện tại. Việc ráp 1 bộ máy để chơi game thực sự trở nên rất nan giải trong thời gian hơn 1 năm trở lại đây, nên việc các dòng APU như Ryzen 3 2200G và Ryzen 5 2400G được tung ra thị trường được xem là những chiếc phao cứu sinh cho những game thủ có kinh phí hạn hẹp đang tìm 1 chiếc máy tính có khả năng chơi game tương đối tốt và dễ dàng nâng cấp về sau này. Trong bài viết này sẽ đánh giá tổng quát hiệu năng của 2 dòng APU trên kiến trúc Zen+ mới nhất của AMD này, để xem liệu việc lựa chọn APU hay dòng card màn hình giá rẻ như GT 1030 thì lựa chọn nào sẽ trở nên phù hợp hơn.</p>\r\n \r\n<p>ROCKET LEAGUE: Tiếp đến ở tựa game Rocket League, ở xung nhịp mặc định, R3 2200G vẫn đạt được mức khung hình trung bình là 54FPS chỉ thấp hơn ~7% so với R5 2400G, 1 lần nữa cho thấy sự cải thiện về hiệu năng chơi game trên R5 2400G là không quá nhiều so với R3 2200G. Với thiết lập kết hợp giữa Medium và High trên độ phân giải 1080p, ở đây sau khi được ép xung R5 2400G vẫn tiếp tục duy trì khung hình trung bình khá tốt ở mức 60FPS tương đương với G4560 và R3 1200 (3.9GHz) với GT 1030, theo ngay sát phía sau là R3 2200G. </p>\r\n<p>Tuy nhiên có 1 vấn đề về Ryzen 2 là việc 0.1% frame time trên cả R3 2200G và R5 2400G gây nên hiện tượng sụt khung hình ở 1 số thời điểm nhất định, mặc dù chỉ với 0.1% frame time thấp thì hiện này là gần như không đáng kể. Nguyên nhân phần lớn có thể là do driver hoặc tựa game Rocket League chưa tung ra bản cập nhật cho kiến trúc Zen+ mới vì hiện tượng này hoàn toàn không xảy ra đối với tựa game Overwatch.</p>\r\n\r\n<p>OVERWATCH: Trên tựa game Overwatch, R3 2200G cũng không hề kém cạnh, khi mà mức khung hình trung bình cũng chỉ thấp hơn 8% so với R5 2400G ở xung nhịp mặc định, còn sau khi được ép xung CPU lên 3.9GHz và 1.65GHz đối với iGPU, hiệu năng của R3 2200G vẫn tiếp tục duy trì khoảng cách ~9% so với R5 2400G. Mặc dù là hiệu năng trên Overwatch của R5 2400G có hơn ~10% so với R3 2200 nhưng xét trên sự chênh lệch về giá thành thì sự chênh lệch này là ko quá đáng kể. Ở xung nhịp mặc định khi ghép R5 2400G với cặp RAM G.Skill Trident Z (3200MHz CL14) đem lại khung hình trung bình xấp xỉ ở mức 54FPS, thấp hơn ~11% so với sau khi được ép xung.</p>\r\n<p>Với thiết lập Medium ở độ phân giải 1080p, có thể thấy ở đây G4560 khi sử dụng với RAM có xung nhịp 2400Mhz và GT 1030 cho mức khung hình trung bình là 63FPS khá tương đồng với R3 1200 (3.9GHz) đi với RAM có xung nhịp 2933MHz và GT 1030 có mức khung hình 61FPS. Trong khi đó R5 2400G sau đi được ép xung CPU lên 3.95GHz và 1.6GHz đối với iGPU (chip đồ họa tích hợp sẵn) cho hiệu năng khá sát ở phía sau với khung hình trung bình là 60FPS.</p>\r\n\r\n<p>CIVILIZATION V: Đối với 1 tựa game có yêu cầu tương đối cao về CPU như Civilization V, việc so sánh hiệu năng cũng có khác biệt so với những tựa game khác, khi mà ở đây sẽ lấy thời gian điều khiển A.I trong game làm thước đo để so sánh hiệu năng thay vì chỉ dùng FPS để so sánh. Tất cả các dòng CPU không có ghi chú gì theo sau thì đều được ghép với bộ RAM có tốc độ là 3200MHz, bạn có thể thấy rõ được R3 2200G sau khi được ép xung (24.7 giây) có hiệu năng cao hơn ~22% so với xung nhịp gốc (27.5 giây).</p>\r\n<p>Trong khi đó, phần CPU của R5 2400G (3.95GHz) có hiệu năng xấp xỉ so với R5 1600X với thời gian là 19.5 giây. Với số lượng người chơi lên tới 5 người thì thời gian này sẽ tăng lên thành 1.6 phút.</p>\r\n\r\n<p> CS: GO : CS: GO là 1 tựa game có thể nói là khá nhạy cảm so với việc thay đổi RAM, với thiết lập tùy chỉnh trên độ phân giải 1080p, R3 2200G với 86FPS, trong khi đó G4560 và GT 1030 dẫn trước khá xa với 120FPS. Nhỉnh hơn chút, ở xung nhịp mặc định, R5 2400G đạt được 95FPS trung bình. Sau khi được ép xung thì mức khung hình của cả R5 2400G và R3 2200G cũng được tăng lên khá đáng kể, nhưng vẫn thấp hơn ~20% so với G4560 và GT 1030, tuy nhiên sự khác biệt giữa R3 2200G và R5 2400G ở đây là rất nhỏ.</p>\r\n\r\n<p>DOTA 2: Chuyển sang DOTA 2 với thiết lập High trên 1080p, cả R3 2200G và R5 2400G đều có hiệu năng tương đồng với nhau sau khi được ép xung, trong khi đó,  khi ghép với GT 1030 thì cả R3 1200 (3.9GHz) và G4560 đều đạt được 63FPS trung bình.</p>\r\n<p>Với xung nhịp mặc định thì kết quả cũng tương tự như vậy với mức khung hình xấp xỉ ở mức 40-45FPS tùy thuộc vào tốc độ và độ trễ của các dòng RAM.</p>\r\n\r\n<p>TOTAL WAR: WARHAMMER: Cuối cùng ở tựa game Total Warhammer, với thiết lập Medium trên 1080p, R3 2200G với 39FPS cao hơn ~11-22% so với G4560 đi với GT 1030 ở xung nhịp mặc định .Hiệu năng của R5 2400 khá tương đồng với G4560 và GT 1030 theo sau là R3 2200G với hiệu năng thấp hơn ~13%. Tuy nhiên sau khi được ép xung lên thì R5 2400G vươn lên vị trí đầu bảng là 43FPS trung bình.</p>\r\n\r\n<p>Nhiệt độ và công suất tiêu thụ: Với việc được tích hợp 1 vi xử lý đồ họa mạnh mẽ (iGPU) nhưng nhiệt độ và công suất tiêu thụ của R3 2200G và R5 2400G là khá tốt với mức TDP chỉ ở 65W, với chiếc tản nhiệt được AMD tặng kèm thì nhiệt độ của R3 2200G sau khi được ép xung (1.4v) vẫn khá tốt (69 độ C) trong quá trình stress test bằng Prime95. Chuyển sang 1 bộ tản nhiệt nước AIO như Kraken X62, giúp cải thiện nhiệt độ hoạt động của R3 2200G xuống đáng kể, chỉ còn 47 độ C.</p>\r\n<p>Trong khi đó về công suất tiêu thụ thì R3 2200G thể hiện khá xuất sắc với 1 việc được tích hợp sẵn 1 iGPU mạnh mẽ như vậy nhưng công suất tiêu thụ thực tế chỉ ở 72W với xung nhịp mặc định và 82W sau khi được ép xung (1.4v).</p>\r\n\r\n<p>Lời kết: Qua các kết quả so sánh, không còn nghi ngờ gì nữa khi nói rằng R3 2200G là 1 lựa chọn có hiệu năng trên giá thành tốt nhất trong phân khúc này ở thời điểm hiện tại, với phần hiệu năng của CPU đạt 78-85% so với R5 2400G với mức giá chỉ gần bằng 1 nửa. Trong khi đó phần iGPU lại không có quá nhiều sự khác biệt giữa 2 dòng APU này, khi mà iGPU của R3 2200G vẫn đủ khả năng để xử lý tốt các tựa game e-Sport ở thiết lập Medium/High trên độ phân giải 1080p.</p>\r\n<p>Mặc dù hiệu năng chơi game của R5 2400G khá tương đồng so với việc sử dụng G4560 và GT 1030 nhưng việc sử dụng APU của AMD sẽ đòi hỏi bạn phải sử dụng các bộ RAM có tốc độ rất nhanh để đạt được hiệu năng mong muốn, cộng thêm tình trạng giá thành RAM hiện nay việc này có thể khiến cho chi phí cao hơn so với G4560 và GT 1030. Cho nên trừ khi bạn có nhu cầu sử dụng những ứng dụng có yêu cầu cao về khả năng xử lý của CPU, thì R3 2200G sẽ là 1 lựa chọn phù hợp hơn rất nhiều đối với 1 bộ máy tính có chi phí thấp.</p>\r\n<p>Hi vọng bài viết này sẽ giúp cho bạn có sự lựa chọn chính xác hơn và dễ dàng hơn cho nhu cầu của mình.</p>\r\n\r\n                                                                                                                                                ', 2489000, 0, '11-AMD-C2-11.jpg', '[\"11-AMD-C2-41.jpg\",\"11-AMD-C2-31.jpg\",\"11-AMD-C2-21.jpg\"]', 100, 36, 1, 1, 234, 0);
